<?php
$title = get_sub_field('title');
$subtitle = get_sub_field('subtitle');
$contactBtn = get_sub_field('contact_btn');
$contactTarget = 'target="_self"';

if(isset($contactBtn['target']) && $contactBtn['target'] != '') {
	$contactTarget = 'target="'.$contactBtn['target'].'"';
}

if (have_rows('regions')) :
	$regions_data = [];

	while (have_rows('regions')) : the_row();
		$region = get_sub_field('region');
		$region_id = strtolower(str_replace(' ', '_', $region));
		$select_states = get_sub_field('select_states');
		$person_image = get_sub_field('person_image');
		$person_name = get_sub_field('person_name');
		$person_position = get_sub_field('person_position');

		$state_values = $select_states ? array_column($select_states, 'value') : [];
		$data_states = implode(';', $state_values);

		$regions_data[] = [
			'region' => $region,
			'region_id' => $region_id,
			'data_states' => $data_states,
			'person_image' => $person_image,
			'person_name' => $person_name,
			'person_position' => $person_position,
		];
	endwhile; ?>

