								<div class="map-person__img">
									<?php 
										$imgSrc = wp_get_attachment_image_src($region_data['person_image'], 'full');
										
										$imgStyle = 'style="background-image: url(' . $imgSrc[0] . ');"';
									?>
									<div class="person-img-inner" <?php echo $imgStyle; ?>>

									</div>
								</div>
							<?php endif; ?>

							<?php if ($region_data['person_name']) : ?>
								<h6><?php echo wp_kses_post($region_data['person_name']); ?></h6>
							<?php endif; ?>

							<?php if ($region_data['person_position']) : ?>
								<span><?php echo wp_kses_post($region_data['person_position']); ?></span>
							<?php endif; ?>
							
							<?php if($contactBtn && !empty($contactBtn)) : ?> 
							<div class="map-person-btn">
								<a href="<?php echo $contactBtn['url']; ?>" <?php echo $contactTarget; ?> class="btn btn-secondary"><?php echo $contactBtn['title']; ?></a>
							</div>
							<?php endif; ?>
						</div>
					<?php endforeach; ?>
				</div>
			</div>
		</div>
	</section>
<?php endif; ?>
